#!/usr/bin/env bash
export DEBIAN_FRONTEND=noninteractive
sudo apt-get update
sudo apt-get install -y build-essential vim curl git mercurial \
  python-dev python-pip libldap2-dev libsasl2-dev

# add github and bitbucket to ssh known hosts
echo "github.com ssh-rsa " | sudo tee -a /etc/ssh/ssh_known_hosts
echo "bitbucket.org ssh-rsa " | sudo tee -a /etc/ssh/ssh_known_hosts

# install relengapi
sudo mkdir /opt/build-relengapi
sudo chown $USER:$USER /opt/build-relengapi
git clone git@github.com:mozilla/build-relengapi.git /opt/build-relengapi
cd /opt/build-relengapi
sudo pip install -e .[test]
sudo pip install anyjson amqp
relengapi createdb

# install transplant blueprint
cd /vagrant
sudo pip install -e .[test]
